#![no_main]
#![no_std] use panic_halt as _; use core::fmt::Write; use cortex_m::peripheral::syst::SystClkSource;
use cortex_m_rt::{entry, exception};
use cortex_m_semihosting::{ debug, hio::{self, HStdout},
}; #[entry]
fn main() -> ! { let p = cortex_m::Peripherals::take().unwrap(); let mut syst = p.SYST; // 配置系统的计时器每秒去触发一个SysTick异常 syst.set_clock_source(SystClkSource::Core); // 这是关于LM3S6965的配置，其有一个12MHz的默认CPU时钟 syst.set_reload(12_000_000); syst.clear_current(); syst.enable_counter(); syst.enable_interrupt(); loop {}
