fn SysTick() { static mut COUNT: u32 = 0; static mut STDOUT: Option<HStdout> = None; *COUNT += 1; // 惰性初始化(Lazy initialization) if STDOUT.is_none() { *STDOUT = hio::hstdout().ok(); } if let Some(hstdout) = STDOUT.as_mut() { write!(hstdout, "{}", *COUNT).ok(); } // 重要信息 如果运行在真正的硬件上，去掉这个 `if` 块， // 否则你的调试器将会以一种不一致的状态结束 if *COUNT == 9 { // 这将终结QEMU进程 debug::exit(debug::EXIT_SUCCESS); }
} tail -n5 Cargo.toml [dependencies]
